import React from 'react';
import './FacCenter.css';
import logo from './IterLogo.png';

function FacCenter() {
  const handleResponse = (name) => {
    // Add response handling logic here
    alert(`Responding to ${name}`);
  };

  const complaints = [
    { name: "Raica Martinez", message: "Linha muito demorada, poderiam ter mais ônibus para rodar." },
    { name: "Virgínia Fonseca", message: "Falta de limpeza nos ônibus e janelas estavam emperradas." }
  ];

  const feedbacks = [
    { name: "Carolina Nepomucena", message: "A musculatura que mantém a espera entre o clima e horários precisa ser melhorada!" },
    { name: "Bianca Soares", message: "App é fácil de acesso e isso facilita a mobilidade" }
  ];

  const lostItems = [
    { name: "Nicolas Petri", message: "Esqueci de retirar minha lancheira em uma viagem no 25 - provavelmente está entre os bancos da frente." },
    { name: "Neymar Jr.", message: "Esqueci minha mochila dentro do ônibus UBS - Vinha no horário de tarde umas 16h." }
  ];

  const renderCard = (title, items) => (
    <div className="fac-card">
      <h2>{title}</h2>
      {items.map((item, index) => (
        <article className="card-item" key={index}>
          <h3>{item.name}</h3>
          <p>{item.message}</p>
          <button onClick={() => handleResponse(item.name)}>RESPONDER</button>
        </article>
      ))}
    </div>
  );

  return (
    <div className="fac-container">
      <header>
        <nav>
          <a href="#voltar"> Central do FAC</a>
        </nav>
        <img src={logo} alt="Logo" className="logo" />
      </header>
     
      <section className="fac-sections">
        {renderCard("Reclamações", complaints)}
        {renderCard("Feedbacks", feedbacks)}
        {renderCard("Itens Perdidos", lostItems)}
      </section>

      <footer>
        <p>Copyright 2024 © IterMob</p>
      </footer>
    </div>
  );
}

export default FacCenter;
