import React, { useState } from 'react';
import './Cadastro.css';
import logo from './IterLogo.png';

function Login() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const usuario = {
      email: email,
      senha: senha,
    };

    try {
      const response = await fetch('https://itermob-back.onrender.com/v1/itermob/loginUsuario', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(usuario),
      });

      const data = await response.json();

      if (response.ok) {
        alert(`Login realizado com sucesso:\nEmail: ${email}`);
        console.log('Resposta do servidor:', data);
      } else {
        console.log('Erro no login:', data);
        alert('Erro no login: ' + (data.message || 'Erro desconhecido'));
      }
    } catch (error) {
      console.error('Erro na requisição:', error);
      alert('Erro ao conectar com o servidor. Verifique sua conexão.');
    }
  };

  return (
    <div>
      <header className="header">
        <img src={logo} alt="Logo" className="logo" />
      </header>
      <div className="cadastro-container">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="email"
              id="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              id="senha"
              placeholder="Senha"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
        <div className="forgot-password">
          <a href="#">Esqueceu a senha?</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
