import React, { useState, useEffect } from 'react';
import './TabelaUsuarios.css';  
import logo from './IterLogo.png';  

function TabelaUsuario() {
  // Define o estado para armazenar os usuários
  const [usuarios, setUsuarios] = useState([]);
  const [erro, setErro] = useState(null);

  // Usa useEffect para fazer o GET quando o componente for montado
  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const response = await fetch('https://itermob-back.onrender.com/v1/itermob/usuarios');
        if (response.ok) {
          const data = await response.json();
          console.log('Dados recebidos:', data); // Verifique os dados no console
          
          // Agora, acessamos a chave "usuarios" diretamente
          setUsuarios(data.usuarios || []); // Garantir que setUsuarios receba o array correto
        } else {
          setErro('Erro ao buscar usuários');
          console.error('Erro ao buscar usuários', response.status);
        }
      } catch (error) {
        setErro('Erro de rede');
        console.error('Erro de rede', error);
      }
    };

    fetchUsuarios();
  }, []); // O array vazio [] faz o fetch rodar apenas uma vez, após a primeira renderização.

  return (
    <div className="usuario-management-container">
      <img src={logo} alt="Logo" className="logo" />     

      <div className="usuario-management-card">
        <h2>GERÊNCIA DE USUÁRIOS</h2>
        
        <table className="usuario-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Email</th>
              <th>Endereço</th>
              <th>Telefone</th>
              <th>Foto de Perfil</th>
            </tr>
          </thead>
          <tbody>
            {erro ? (
              <tr>
                <td colSpan="6">{erro}</td>
              </tr>
            ) : (
              usuarios.length > 0 ? (
                usuarios.map(usuario => (
                  <tr key={usuario.id}>
                    <td>{usuario.id}</td>
                    <td>{usuario.nome} {usuario.sobrenome}</td>
                    <td>{usuario.email}</td>
                    <td>{usuario.endereco || 'Não disponível'}</td>
                    <td>{usuario.telefone}</td>
                    <td>
                      {usuario.foto_perfil ? (
                        <img src={usuario.foto_perfil} alt={`${usuario.nome} ${usuario.sobrenome}`} className="foto-perfil" />
                      ) : (
                        'Sem foto'
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6">Nenhum usuário encontrado.</td>
                </tr>
              )
            )}
          </tbody>
        </table>
      </div>

      <footer>
        <p>Copyright 2024 © IterMob</p>
      </footer>
    </div>
  );
}

export default TabelaUsuario;
