import React, { useState } from 'react';
import './UserDetails.css';
import logo from './IterLogo.png';
import Cartao1 from './cartao1.png'; 
import Cartao2 from './cartao2.png';  // Imagem do segundo cartão
import Cartao3 from './cartao3.png';  // Imagem do terceiro cartão

function UserDetails() {
  // Estado para armazenar o cartão selecionado
  const [selectedCard, setSelectedCard] = useState('4578');

  const handleCardChange = (event) => {
    setSelectedCard(event.target.value);
  };

  return (
    <div className="user-details-container">
      <img src={logo} alt="Logo" className="logo" />     

      <div className="user-details-card">
        <div className="user-icon">
          <img src="https://via.placeholder.com/100" alt="User Icon" className="user-image" />
        </div>
        
        <div className="user-info">
          <p><strong>Nome:</strong> Bianca Soares</p>
          <p><strong>E-mail:</strong> bia@gmail</p>
          <p><strong>Endereço:</strong> Estrada dos Pereiras</p>
          <p><strong>Telefone:</strong> 11 93717-7249</p>
          <p><strong>Cartões:</strong>
            <span className="card-info">
              <img 
                src={selectedCard === '4578' ? Cartao1 : selectedCard === '1234' ? Cartao2 : Cartao3} 
                alt="Cartão logo" 
                className="card-logo" 
              />
              <span>....{selectedCard}</span>
            </span>
          </p>

          <select value={selectedCard} onChange={handleCardChange}>
            <option value="4578">4578</option>
            <option value="1234">1234</option>
            <option value="9999">9999</option>
          </select>
        </div>
      </div>

      <footer>
        <p>Copyright 2024 © IterMob</p>
      </footer>
    </div>
  );
}

export default UserDetails;
